describe ('user publishes new story', function(){

    it ('publishes new story', function(){

        browser.get('https://stg.waroncancer.com/');
        browser.driver.manage().window().maximize();

        //Entering username and password

        element(by.css('.auth0-lock-input')).sendKeys('hidromehanika6@gmail.com');
        element(by.name('password')).sendKeys('inter1908');

        //Clicking on popup accept button

        element(by.css('.cl-accept')).click();
        browser.sleep(3000);

        //Clicking on login button

        element(by.css('.auth0-label-submit')).click();
        browser.sleep(3000);

        element(by.cssContainingText('[href*="/story"]', 'Create new Story')).click();
        browser.sleep(2000);

        //User sets content of new story

        element(by.css('[ng-model="vm.story.title"]')).sendKeys('AutoTest1');
        browser.sleep(2000);
        element(by.css('[ng-model="vm.story.content"]')).sendKeys('trezfdhue');
        browser.sleep(2000);

        //User published new story

        element(by.id('storyActionMenu')).click();
        browser.sleep(2000);
        element(by.css('[ng-click="vm.publishStory()"]')).click();
        browser.sleep(4000);
    });

});