describe ('user publishes new post', function(){

    it ('publishes new post', function(){

        browser.get('https://stg.waroncancer.com/');
        browser.driver.manage().window().maximize();

        //Entering username and password

        element(by.css('.auth0-lock-input')).sendKeys('hidromehanika6@gmail.com');
        element(by.name('password')).sendKeys('inter1908');

        //Clicking on popup accept button

        element(by.css('.cl-accept')).click();
        browser.sleep(3000);

        //Clicking on login button

        element(by.css('.auth0-label-submit')).click();
        browser.sleep(3000);

        //Choosing settings menu

        element(by.id('navbarDropdownProfile')).click();
        browser.sleep(2000);
        element(by.cssContainingText('[href*="/settings"]', 'Settings')).click();
        browser.sleep(2000);

        //Editing first/last name/Description text/date of birth/City

        element(by.css('[ng-model="vm.model.firstName"]')).sendKeys('steven');
        browser.sleep(2000);
        element(by.css('[ng-model="vm.model.lastName"]')).sendKeys('gerrard');
        browser.sleep(2000);
        element(by.css('[ng-model="vm.model.about"]')).sendKeys('lalalalajajaja');
        browser.sleep(2000);
        element(by.css('[ng-model="vm.model.birthDate"]')).sendKeys('22-02-2001');
        browser.sleep(2000);
        element(by.css('[ng-model="vm.model.city"]')).sendKeys('Sarajev');
        browser.sleep(2000);

        //Savign changes and navigating to home page

        element(by.buttonText('Save changes')).click();
        browser.sleep(2000);
        element(by.cssContainingText('[href*="/feed"]', 'Home')).click();
        browser.sleep(2000);
        
    });
});