describe ('login with google account', function(){

    it ('logs in user with google account', function(){

        browser.get('https://stg.waroncancer.com/');
        browser.driver.manage().window().maximize();

        element(by.css('[ng-click="vm.showSignUp()"]')).click();

        browser.sleep(3000);

        element(by.buttonText('Sign up with Google')).click();
        browser.sleep(3000);

        //Bypassing non-angular login form

        browser.ignoreSynchronization = true;
        browser.waitForAngular();
        browser.sleep(500); 

        //Logging with false credentials

        element(by.name('identifier')).sendKeys('mop123@gmail.com');
        element(by.css('.CwaK9')).click();

        browser.sleep(4000);

        element(by.name('password')).sendKeys('1234hdje');
        element(by.css('.CwaK9')).click();

        browser.sleep(3000);

    });

});